function myfunload() {
    $(".panel-a").mobilepanel();
    if ($("#menuProduct").size() == 1) {
        $("#moProduct li[data-menu='menuProduct']").append("<ul></ul>");
        $("#menuProduct li").clone().appendTo($("#moProduct li[data-menu='menuProduct'] ul"));
        $("#menuProduct li").clone().appendTo($("#listmoProduct"));
    }
    $(".fullbox-img").bgsizebox({
        fimg: true,
        imgcss: "hideo",
        attrname: "data-src"
    });

    $(".search-link").click(function () {
        var idp = $(this).attr("href");
        $(this).toggleClass("open");
        if ($(this).hasClass("open")) {
            $(idp).fadeIn(500);
        } else {
            $(idp).fadeOut(500);
        }
        return false;
    });
    if ($("#jcarouselDetails").size() == 1) {
        $("#jcarouselDetails").jcarouselzoom({
            autos: false,
            pausehover: false,
            pager: false, //pager num
            timespees: 3000,
            thumswrap: false, // thums sroll
            shownum: 5, // show number thums
            usezoom: true
        });
    }
    myfunsroll();
    if ($("#productw").size() == 1) {
        pagerresize();
    }
    if ($("#result").size() == 1) {
        mymapfun();
    }
}
