function mymapfun() {
    mymapload();
    var myclearmap;
    clearTimeout(myclearmap);
    $(window).resize(function () {
        $("#mapwrap").remove();
        myclearmap = setTimeout(function () {
            mymapload();
        }, 500);
    });
}
function mymapload() {
    if ($(window).width() > 767) {
        $("#result").load("map.aspx #mapwrap", function (response, status, xhr) {
            if (status == "success") {
                mymap();
            }
            if (status == "error") {
                var msg = "Sorry but there was an error: ";
                $("#error").html(msg + xhr.status + " " + xhr.statusText);
            }
        });
    }
}
function mymap() {

    // Defining variables that need to be available to some functions
    var map, infoWindow;
    var rendererOptions = {
        draggable: true
    };
    var directionsDisplay;
    var directionsService = new google.maps.DirectionsService();
    //Thay dia chi web
    var endAddress = "10.755542, 106.653918";
    //Thay toa do
    var lat = 10.755542, long = 106.653918;
    mufunmap();
    $("#getdiretion").click(function () {
        calcRoute();
        return false;
    });
    //function
    function mufunmap() {
        //init direction
        directionsDisplay = new google.maps.DirectionsRenderer(rendererOptions);
        // Creating a map
        var options = {
            zoom: 17,
            center: new google.maps.LatLng(lat, long),
            mapTypeId: google.maps.MapTypeId.ROADMAP
        };

        map = new google.maps.Map(document.getElementById('map'), options);


        // Adding a marker
        var marker = new google.maps.Marker({
            position: new google.maps.LatLng(lat, long),
            map: map,
            title: 'Click me'
        });
        // Adding a marker


        google.maps.event.addListener(marker, 'click', function () {

            // Check to see if an InfoWindow already exists
            if (!infoWindow) {
                infoWindow = new google.maps.InfoWindow();
            }

            // Creating the content  
            var content = '<div class="box">' +
                '<h2>Phụ tùng xe máy Liko</h2>' +
                '<div class="wrap-mapbox">' +
                    '<p>153 Tân Thành, Phường 15, Quận 5, Tp. HCM</p>' +
                    '<p>el: (08) 6259 7622 - 0988.985.105 - Email: <a href="mailto:tmlh@gmail.com">tmlh@gmail.com</a></p>' +
                '</div>' +
            '</div>';

            // Setting the content of the InfoWindow
            infoWindow.setContent(content);

            // Opening the InfoWindow
            infoWindow.open(map, marker);

        });

        // Hiển thị thông tin
        google.maps.event.trigger(marker, 'click');

        directionsDisplay.setMap(map);
        directionsDisplay.setPanel(document.getElementById("directionsPanel"));
    };
    function calcRoute() {
        var start = document.getElementById("start").value;
        var end = endAddress;
        var request = {
            origin: start,
            destination: end,
            travelMode: google.maps.DirectionsTravelMode.WALKING
            //travelMode: google.maps.DirectionsTravelMode.BICYCLING
            //travelMode: google.maps.DirectionsTravelMode.DRIVING
        };
        directionsService.route(request, function (response, status) {
            if (status == google.maps.DirectionsStatus.OK) {
                directionsDisplay.setDirections(response);
            }
        });
    }
}